import React from "react";
//Import Icons
import serviceCops from "../img/service_cops.png";
import neptune from "../img/neptune.png";

import schoolpay from "../img/schoolpay.png";
import gulapay from "../img/gulapay.png";
import seepug from "../img/seepug.png";
import wefarm from "../img/wefarm.png";
//Styles
import { About, DescriptionOurClients, Image } from "../styles";
import styled from "styled-components";
import { scrollReveal } from "../animation";
import { useScroll } from "./useScroll";

const OurClients = () => {
  const [element, controls] = useScroll();
  return (
    <Services
      variants={scrollReveal}
      animate={controls}
      initial="hidden"
      ref={element}
    >
      <DescriptionOurClients>
        <Cards>
          <Card>
            <div className="icon">
              <div className="inner_circle">
                <i class="uil uil-truck"></i>
              </div>
            </div>
          </Card>
          <Card>
            <div className="icon">
              <img alt="icon" src={neptune} />
            </div>
          </Card>
          <Card>
            <div className="icon">
              <img alt="icon" src={schoolpay} />
            </div>
          </Card>
          <Card>
            <div className="icon">
              <img alt="icon" src={gulapay} />
            </div>
          </Card>
          <Card>
            <div className="icon">
              <img alt="icon" src={seepug} />
            </div>
          </Card>
          <Card>
            <div className="icon">
              <img alt="icon" src={wefarm} />
            </div>
          </Card>
        </Cards>
      </DescriptionOurClients>
    </Services>
  );
};

const Services = styled(About)`
  text-align: center;
  background: white;
  margin-top: 0.8rem;

  p {
    width: 70%;
    padding: 2rem 0rem 4rem 0rem;
  }
`;

const Cards = styled.div`
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  column-gap: 1srem;

  @media (max-width: 1300px) {
    justify-content: center;
  }
`;
const Card = styled.div`
  flex-basis: 15rem;
  .icon {
    display: flex;
    align-items: center;
    background: #e4b2d6;
    height: 10rem;
    width: 10rem;

    border-radius: 50%;
    .inner_circle{
        height: 8rem;
    width: 8rem;
    border-radius: 50%;
    border: 5px solid white;
    }
  }
`;

export default OurClients;
