import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import { useLocation } from "react-router-dom";
import home1 from "../img/lolog.png";

const Nav = () => {
  const { pathname } = useLocation();
  return (
    <StyledNav>
      <LogoContainer>
        <Link id="logo" to="/">
          <img src={home1} alt="guy with a camera" />
        </Link>
      </LogoContainer>
      <ul>
        <li>
          <Link to="/">Services</Link>
          <Line
            transition={{ duration: 0.75 }}
            initial={{ width: "0%" }}
            animate={{ width: pathname === "/" ? "50%" : "0%" }}
          />
        </li>
        <li>
          <Link to="/work">Portfolio</Link>
          <Line
            transition={{ duration: 0.75 }}
            initial={{ width: "0%" }}
            animate={{ width: pathname === "/work" ? "50%" : "0%" }}
          />
        </li>
        <li>
          <Link to="/contact">Packages</Link>
          <Line
            transition={{ duration: 0.75 }}
            initial={{ width: "0%" }}
            animate={{ width: pathname === "/contact" ? "50%" : "0%" }}
          />
        </li>
        <li>
          <Link to="/contact">Company</Link>
          <Line
            transition={{ duration: 0.75 }}
            initial={{ width: "0%" }}
            animate={{ width: pathname === "/contact" ? "50%" : "0%" }}
          />
        </li>
        <li>
          <Link to="/contact">Blog</Link>
          <Line
            transition={{ duration: 0.75 }}
            initial={{ width: "0%" }}
            animate={{ width: pathname === "/contact" ? "50%" : "0%" }}
          />
        </li>
      </ul>
      <StyledButton>
        <Link to="/contact">Letâ€™s create magic</Link>
      </StyledButton>
    </StyledNav>
  );
};

const StyledNav = styled.nav`
  min-height: 7.125rem;
  display: flex;
  margin: auto;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 7rem;
  background: #000000;
  opacity: 0.95;
  backdrop-filter: blur(2px);
  position: sticky;
  top: 0;
  z-index: 10;
  a {
    color: white;
    text-decoration: none;
  }
  ul {
    display: flex;
    list-style: none;
  }
  #logo {
    font-size: 1.5rem;
    font-family: "Lobster", cursive;
    font-weight: lighter;
  }
  li {
    padding-left: 10rem;
    position: relative;
  }
  @media (max-width: 1300px) {
    flex-direction: column;
    padding: 2rem 1rem;
    #logo {
      display: inline-block;
      margin: 1rem;
    }
    ul {
      padding: 2rem;
      justify-content: space-around;
      width: 100%;
      li {
        padding: 0;
      }
    }
  }
`;

const Line = styled(motion.div)`
  height: 0.3rem;
  background: #23d997;
  width: 0%;
  position: absolute;
  bottom: -80%;
  left: 60%;
  @media (max-width: 1300px) {
    left: 0%;
  }
`;

const LogoContainer = styled.div`
  z-index: 2;
  flex: 1;
  overflow: hidden;
  img {
    width: 144px;
    height: 56px;
  }
`;
const StyledButton = styled.button`
  box-sizing: border-box !important;


  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 13px 31px;
  gap: 10px;

  width: 260px;
  height: 66px;
  background-image: linear-gradient(to right bottom, #ff0000 #ffcc08);
  border-radius: 100px;

  /* Inside auto layout */
  flex: none;
  order: 1;
  flex-grow: 0;
  position: relative;

  &::after{
    position: absolute;
    top: -2%;
    bottom:-2%;
    left:-2%;
    right: -2%;
    height: 100%
    ;
  }
`;

export default Nav;
