import React from "react";
//Import Icons
import serviceCops from "../img/category-1.jpg";
import neptune from "../img/category-3.jpg";
import { motion } from "framer-motion";
import schoolpay from "../img/category-3.jpgg";
import gulapay from "../img/gulapay.png";
import seepug from "../img/seepug.png";
import wefarm from "../img/wefarm.png";
//Styles
import { About, DescriptionVegetables, Image } from "../styles";
import styled from "styled-components";
import { scrollReveal } from "../animation";
import { useScroll } from "./useScroll";
import { fade } from "../animation";

const Vegetables = () => {
    const [element, controls] = useScroll();
    return (
        <VegetablesContainer
            variants={scrollReveal}
            animate={controls}
            initial="hidden"
            ref={element}
        >
            <DescriptionVegetables>
                <Cards>
                    <Card>
                        <Image>
                            <img src={serviceCops} alt="guy with a camera" />
                        </Image>
                        <motion.button variants={fade}>Fruits </motion.button>
                    </Card>

                    <Card>
                        <Image>
                            <img src={serviceCops} alt="guy with a camera" />
                        </Image>
                        <motion.button variants={fade}>Juices</motion.button>
                    </Card>

                    <Card>
                        <Image>
                            <img src={schoolpay} alt="guy with a camera" />
                        </Image>
                        <motion.button variants={fade}>Vegetables</motion.button>
                    </Card>

                    <Card>
                        <Image>
                            <img src={serviceCops} alt="guy with a camera" />
                        </Image>
                        <motion.button variants={fade}>View Details</motion.button>
                    </Card>

                    <Card>
                        <Image>
                            <img src={serviceCops} alt="guy with a camera" />
                        </Image>
                        <motion.button variants={fade}>Dried</motion.button>
                    </Card>

                    <Card>
                        <Image>
                            <img src={neptune} alt="guy with a camera" />
                        </Image>
                        <motion.button variants={fade}>View Details</motion.button>
                    </Card>


                </Cards>

            </DescriptionVegetables>
        </VegetablesContainer>
    );
};

const VegetablesContainer = styled(About)`
  text-align: center;
  background: white;
  height: 100%;
  width:100%;


`;

const Cards = styled.div`
display: grid;
  grid-template-columns: repeat(auto-fill, minmax(25rem, 1fr));
  width: 80%;
  margin: 0 auto;

  @media (max-width: 1300px) {
    justify-content: center;
  }
`;

const Card = styled.div`
  display:flex;
  align-items:center;
  justify-content: center;
  flex-direction: column;
  row-gap: 1rem;
  margin: 2rem;
  position:relative;

  button{
    background: #82ae46;
    position:absolute;
    bottom:0;
    left: 0;
    z-index:100;
  }
  img{
    width: 100%;
    height: 250px;
    }

`;

export default Vegetables;
