import React from "react";
//Import Icons
import serviceCops from "../img/service_cops.png";
import neptune from "../img/neptune.png";

import schoolpay from "../img/schoolpay.png";
import gulapay from "../img/gulapay.png";
import seepug from "../img/seepug.png";
import wefarm from "../img/wefarm.png";
//Styles
import { About, DescriptionServices, Image } from "../styles";
import styled from "styled-components";
import { scrollReveal } from "../animation";
import { useScroll } from "./useScroll";

const OurServices = () => {
  const [element, controls] = useScroll();
  return (
    <Services
      variants={scrollReveal}
      animate={controls}
      initial="hidden"
      ref={element}
    >
      <DescriptionServices>
        <h2>Our Clients</h2>
        <Cards>
          <Card>
            <div className="icon">
              <img alt="icon" src={serviceCops} />
              p
            </div>
          </Card>

          <Card>
            <div className="icon">
              <img alt="icon" src={neptune} />
            </div>
          </Card>

          <Card>
            <div className="icon">
              <img alt="icon" src={schoolpay} />
            </div>
          </Card>
        </Cards>
      </DescriptionServices>
    </Services>
  );
};

const Services = styled(About)`
  text-align: center;
  background: #010101;
  height: 60vh;
  margin-top: 1rem;
  h2 {
    padding-bottom: 1.5rem;
    font-size: 2.2rem;
    font-weight: bolder;
  }
  p {
    width: 70%;
    padding: 2rem 0rem 4rem 0rem;
  }
`;

const Cards = styled.div`
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  column-gap: 1srem;

  @media (max-width: 1300px) {
    justify-content: center;
  }
`;
const Card = styled.div`
  flex-basis: 30rem;
  height: 491px;

  background: #0b0b0b;
  border-radius: 20px;

  /* Inside auto layout */
  .icon {
    display: flex;
    align-items: center;

    img {
      width: 8rem;
      object-fit: cover;
    }
  }
`;

export default OurServices;
