  bottom: -80%;
  left: 60%;
  @media (max-width: 1300px) {
    left: 0%;
  }
`;

const LogoContainer = styled.div`
  z-index: 2;
  flex: 1;
  overflow: hidden;
  img {
    width: 144px;
    height: 56px;
  }
`;
const StyledButton = styled.button`
  box-sizing: border-box !important;

  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  padding: 13px 31px;
  gap: 10px;
  margin-left: 2rem;
  height: 35px;

  border-radius: 100px !important;

  /* Inside auto layout */
  flex: none;
