  &::after {
    position: absolute;
    top: -2%;
    bottom: -2%;
    left: -2%;
    right: -2%;
    height: 100%;
    width: 100%;
    background-image: linear-gradient(
      to right bottom,
      #ff0000 #ffcc08
    ) !important;
  }
