import React from "react";
//Import Icons
import brandDesign from "../img/brandDesign.png";
import productDesign from "../img/productDesign.png";

import digitalMarketing from "../img/digitalMarketing.png";
import gulapay from "../img/gulapay.png";
import seepug from "../img/seepug.png";
import wefarm from "../img/wefarm.png";
//Styles
import { About, DescriptionServices, Image } from "../styles";
import styled from "styled-components";
import { scrollReveal } from "../animation";
import { useScroll } from "./useScroll";

const OurServices = () => {
  const [element, controls] = useScroll();
  return (
    <Services
      variants={scrollReveal}
      animate={controls}
      initial="hidden"
      ref={element}
    >
      <DescriptionServices>
        <h2>Our Services</h2>
        <Cards>
          <Card>
            <div className="icon">
              <img alt="icon" src={brandDesign} />
              <h4 className="card_title">Brand Design</h4>
              <p>
                We have put a number of corporate brands into a better market
                position simply by the revamping them.
              </p>
            </div>
          </Card>

          <Card>
            <div className="icon">
              <img alt="icon" src={productDesign} />
              <h4 className="card_title">Product Design</h4>
              <p>
                We have put a number of corporate brands into a better market
                position simply by the revamping them.
              </p>
            </div>
          </Card>

          <Card>
            <div className="icon">
              <img alt="icon" src={digitalMarketing} />
              <h4 className="card_title">Brand Design</h4>
              <p>
                We have put a number of corporate brands into a better market
                position simply by the revamping them.
              </p>
            </div>
          </Card>
        </Cards>
      </DescriptionServices>
    </Services>
  );
};

const Services = styled(About)`
  text-align: center;
  background: #010101;
  height: 80vh;
  margin-top: 0.8rem;
  h2 {
    padding-bottom: 2.5rem;
    font-size: 2.2rem;
    font-weight: bolder;
  }
`;

const Cards = styled.div`
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  column-gap: 1rem;

  @media (max-width: 1300px) {
    justify-content: center;
  }
`;

const Card = styled.div`
  flex-basis: 24rem;
  min-height: 28rem;
  overflow: hidden;
  background: #0b0b0b;
  border-radius: 20px;

  /* Inside auto layout */
  .icon {
    display: flex;
    flex-direction: column;
    width: 100%;
    overflow: hidden;

    img {
      width: 100%;
      height: 15rem;
      object-fit: cover;
    }
    h4 {
      font-size: 1.5rem;
      align-self: flex-start;
      width: 90%;
      text-align: left;
      margin: 1.3rem auto 0 auto;
    }
    p {
      font-size: 1.2rem;
      width: 90%;
      padding: 1.5rem 0;
      align-self: flex-start;
      color: #949494;
    }
  }
`;

export default OurServices;
