import styled from "styled-components";
import { motion } from "framer-motion";

export const About = styled(motion.div)`
  height: 93vh;
  display: flex;
  align-items: center;
  justify-content: space-between;
  overflow: hidden;
  color: white;
  position: relative;

  @media (max-width: 1300px) {
    display: block;
    padding: 2rem 2rem;
    text-align: center;
  }
`;
export const DescriptionAbout = styled.div`
  position: absolute;
  top: 70%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 3;
  width: 50%;
  text-align: center;

  h2 {
    font-weight: bolder;
    width: 100%;
    font-size: 2.3rem;
  }
  button {
    padding: 13px 31px;
    border: none !important;
    background: #dfb207;
    border-radius: 10px;
    margin-top: 2rem;

    font-family: "Baloo 2";
    font-style: normal;
    font-weight: 600;

    /* identical to box height */
    text-align: center;

    color: #000000;
  }
  @media (max-width: 1300px) {
    padding: 0;
    button {
      margin: 2rem 0rem 5rem 0rem;
    }
  }
`;
export const Ellipse = styled.div`
  position: absolute;
  top: 80%;
  left: 50%;
  width: 60%;
  height: 70%;
  border-radius: 50%;

  background: #010101;
  filter: blur(50px);
  box-shadow: 0 0 400px #010101;
  transform: translate(-50%, -40%);
  z-index: 2;
`;
export const Description = styled.div`
  flex: 1;
  padding-right: 5rem;
  z-index: 2;

  h2 {
    font-weight: lighter;
  }
  @media (max-width: 1300px) {
    padding: 0;
    button {
      margin: 2rem 0rem 5rem 0rem;
    }
  }
`;
export const DescriptionOurClients = styled.div`
  flex: 1;
  z-index: 2;

  h2 {
    font-weight: lighter;
  }
  @media (max-width: 1300px) {
    padding: 0;
    button {
      margin: 2rem 0rem 5rem 0rem;
    }
  }
`;
export const DescriptionServices = styled.div`
  flex: 1;
  z-index: 2;

  h2 {
    font-weight: lighter;
  }
  @media (max-width: 1300px) {
    padding: 0;
    button {
      margin: 2rem 0rem 5rem 0rem;
    }
  }
`;
export const DescriptionMission = styled.div`
  flex: 1;
  z-index: 2;

  h2 {
    font-weight: lighter;
  }
  @media (max-width: 1300px) {
    padding: 0;
    button {
      margin: 2rem 0rem 5rem 0rem;
    }
  }
`;

export const Image = styled.div`
  z-index: 2;
  flex: 1;
  overflow: hidden;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

export const Hide = styled.div`
  overflow: hidden;
`;
